let btnLogin=document.querySelector(".btn"),username="",password="";btnLogin.addEventListener("click",function(e){if(username=document.querySelector("#username").value,password=document.querySelector("#password").value,postUserLogin())postUserLogin();else{let e=document.querySelector(".form__error");e.style.color="#B60A19",e.innerHTML="It must be a valid username and password"}username=document.querySelector("#username").value="",password=document.querySelector("#password").value=""});let postUserLogin=()=>{fetch("http://localhost:3000/users/login",{method:"post",headers:{"Content-Type":"application/json",Authorization:"Bearer "+localStorage.getItem("token")},body:JSON.stringify({username:username,password:password})}).then(e=>e.json()).then(e=>{if(console.log(e),"success"===e.status){let o=e.data.token;localStorage.setItem("token",o),window.location.href="/"}})};